mapper.select:
select board.bo_no, bo_writer, bo_pass, bo_ip, bo_mail, bo_title,
	   bo_content, bo_date, bo_hit, bo_rcmd
	   , pds_no, pds_savename, pds_filename, pds_size, pds_fancysize
	   ,pds_mime
from board left outer join pds on (board.bo_no=pds.bo_no)
where board.bo_no = #{bo_no}

mapper.update:
		UPDATE board
    	SET bo_hit = bo_hit+1
		WHERE BO_NO = #{bo_no}

		UPDATE board


    			bo_mail = #{bo_mail},


    		bo_title = #{bo_title},


			bo_content = #{bo_content}


    	WHERE BO_NO = #{bo_no}
